DROP TABLE IF EXISTS emlog_attachment;
CREATE TABLE emlog_attachment (
   aid smallint(5) unsigned NOT NULL auto_increment,
   blogid mediumint(8) unsigned NOT NULL,
   filename varchar(255) NOT NULL,
   filesize int(10) NOT NULL,
   filepath varchar(255) NOT NULL,
   addtime bigint(20) NOT NULL,
   PRIMARY KEY (aid),
   KEY blogid (blogid)
);


DROP TABLE IF EXISTS emlog_blog;
CREATE TABLE emlog_blog (
   gid mediumint(8) unsigned NOT NULL auto_increment,
   title varchar(255) NOT NULL,
   date bigint(20) NOT NULL,
   content longtext NOT NULL,
   excerpt longtext NOT NULL,
   alias varchar(200) NOT NULL,
   author int(10) DEFAULT '1' NOT NULL,
   sortid tinyint(3) DEFAULT '-1' NOT NULL,
   type varchar(20) DEFAULT 'blog' NOT NULL,
   views mediumint(8) unsigned NOT NULL,
   comnum mediumint(8) unsigned NOT NULL,
   tbcount mediumint(8) unsigned NOT NULL,
   attnum mediumint(8) unsigned NOT NULL,
   top enum('n','y') DEFAULT 'n' NOT NULL,
   hide enum('n','y') DEFAULT 'n' NOT NULL,
   allow_remark enum('n','y') DEFAULT 'y' NOT NULL,
   allow_tb enum('n','y') DEFAULT 'y' NOT NULL,
   password varchar(255) NOT NULL,
   PRIMARY KEY (gid),
   KEY date (date),
   KEY author (author),
   KEY sortid (sortid),
   KEY type (type),
   KEY hide (hide)
);

